34;dom&#34;&gt;&lt;/ul&gt;&lt;h2&gt;&lt;a href=&#34;data:,&#34; id=&#34;link&#34;&gt;Rendered view&lt;/a&gt;: (&lt;a href=&#34;#&#34; onclick=&#34;toggleVisibility(this); return false;&#34;&gt;hide&lt;/a&gt;&lt;!--, &lt;a href=&#34;#&#34; onclick=&#34;grow(this)&#34;&gt;grow&lt;/a&gt;--&gt;):&lt;/h2&gt;&lt;p&gt;&lt;iframe src=&#34;00 empty html.html&#34;&gt;&lt;/iframe&gt;&lt;/p&gt; &lt;!-- data:, --&gt;&lt;h2&gt;innerHTML view: (&lt;a href=&#34;#&#34; onclick=&#34;toggleVisibility(this); return false;&#34;&gt;show&lt;/a&gt;, &lt;a href=&#34;#&#34; onclick=&#34;updateDOM()&#34;&gt;refresh&lt;/a&gt;):&lt;/h2&gt;&lt;pre class=&#34;hidden&#34;&gt;&amp;lt;!DOCTYPE HTML&gt;&amp;lt;html&gt;&lt;samp&gt;&lt;/samp&gt;&amp;lt;/html&gt;&lt;/pre&gt;&lt;h2&gt;Log: (&lt;a href=&#34;#&#34; onclick=&#34;toggleVisibility(this); return false;&#34;&gt;hide&lt;/a&gt;):&lt;/h2&gt;&lt;pre id=&#34;log&#34;&gt;Script not loaded.&lt;/pre&gt;&lt;script&gt;var iframe = document.getElementsByTagName(&#39;iframe&#39;)[0];var textarea = document.getElementsByTagName(&#39;textarea&#39;)[0];var pre = document.getElementsByTagName(&#39;samp&#39;)[0];var dom = document.getElementsByTagName(&#39;ul&#39;)[0];var log = document.getElementById(&#39;log&#39;);var networkStatus = document.getElementById(&#39;network-status&#39;);var delayedInputUpdater = 0;var delayedDOMUpdater = 0;var lastString = &#39;&#39;;function updateInput(event) {if (delayedInputUpdater)clearTimeout(delayedInputUpdater);delayedInputUpdater = setTimeout(update, 100);}function updateIframe() {while (log.firstChild)log.removeChild(log.lastChild);iframe.contentWindow.document.open();iframe.contentWindow.onerror = function (a, b, c) {record(&#39;error: &#39; &#43; a &#43; &#39; on line &#39; &#43; c);}iframe.contentWindow.w = function (s) {if ((iframe.contentWindow.Event) &amp;&amp; (s instanceof iframe.contentWindow.Event)) { // why doesn&#39;t this work?var target = s.target;if (target instanceof iframe.contentWindow.Node) {if (target instanceof iframe.contentWindow.Element) {target = &#39;&lt;&#39; &#43; target.nodeName;if (target.id) {target &#43;= &#39; id=&#34;&#39; &#43; target.id &#43; &#39;&#34;&#39;;}target &#43;= &#39;&gt;&#39;;} else {// ...}} else {target = &#39;&#34;&#39; &#43; target &#43; &#39;&#34;&#39;}var interface = s.toString();var bubbles = s.bubbles ? &#39;; bubbles&#39; : &#39;&#39;;var cancelable = s.cancelable ? &#39;; cancelable&#39; : &#39;&#39;;record(&#39;log: event &#34;&#39; &#43; s.type &#43; &#39;&#34; using interface &#34;&#39; &#43; interface &#43; &#39;&#34; targetted at &#39; &#43; target &#43; &#39;&#39; &#43; bubbles &#43; cancelable);} else if (typeof s == &#39;object&#39;) {var n = 0;props = &#39;&#39;;for (var i in s) {n &#43;= 1;if (n &lt; 5) {if (n == 1) {props &#43;= &#39;: &#39;;} else {props &#43;= &#39;, &#39;;}if (typeof s[i] == &#39;string&#39; || typeof s[i] == &#39;object&#39;)props &#43;= i &#43; &#39;=&#34;&#39; &#43; s[i] &#43; &#39;&#34;&#39;;elseprops &#43;= i &#43; &#39;=&#39; &#43; s[i];}}if (n &gt;= 5) {props &#43;= &#39;...&#39;;}props = props.replace(/\n/g, &#39;\\n&#39;);record(&#39;log: object &#34;&#39; &#43; s &#43; &#39;&#34; (&#39; &#43; n &#43; &#39; props&#39; &#43; props &#43; &#39;)&#39;);} else {record(&#39;log: &#39; &#43; s);}}iframe.contentWindow.document.write(textarea.value);iframe.contentWindow.document.close();}function update() {if (lastString != textarea.value) {document.getElementById(&#39;link&#39;).href = &#39;data:text/html;charset=utf-8,&#39; &#43; encodeURIComponent(textarea.value);try {updateIframe(); // clears log} catch (e) {var p = iframe.parentNode;p.removeChild(iframe);iframe = document.createElement(&#39;iframe&#39;);iframe.setAttribute(&#39;src&#39;, &#39;blank.html&#39;);p.appendChild(iframe);updateIframe();record(&#39;internal error: &#39; &#43; e.message);}lastString = textarea.value;if (delayedDOMUpdater)