ldNodes[i].attributes)for (var j = 0; j &lt; node.childNodes[i].attributes.length; j &#43;= 1) {if (node.childNodes[i].attributes[j].specified) {var attName = document.createElement(&#39;code&#39;);attName.appendChild(document.createTextNode(node.childNodes[i].attributes[j].nodeName));attName.className = &#39;attribute name&#39;;var attValue = document.createElement(&#39;code&#39;);attValue.appendChild(document.createTextNode(node.childNodes[i].attributes[j].nodeValue));attValue.className = &#39;attribute value&#39;;var att = document.createElement(&#39;span&#39;);att.className = &#39;t2&#39;;att.appendChild(attName);att.appendChild(document.createTextNode(&#39;=&#34;&#39;));att.appendChild(attValue);att.appendChild(document.createTextNode(&#39;&#34;&#39;));li.appendChild(document.createTextNode(&#39; &#39;));li.appendChild(att);}}if (node.childNodes[i].parentNode == node) {if (node.childNodes[i].childNodes.length) {var ul2 = document.createElement(&#39;ul&#39;);li.appendChild(ul2);printDOM(ul2, node.childNodes[i]);}if (node.childNodes[i].content) {var ul2 = document.createElement(&#39;ul&#39;);li.appendChild(ul2);ul2.className = &#39;template&#39;;printDOM(ul2, node.childNodes[i].content);}} else {li.className &#43;= &#39; misparented&#39;;}ul.appendChild(li);}}function toggleVisibility(link) {var n = link.parentNode.nextSibling;if (n.nodeType == 3 /* text node */) n = n.nextSibling; // we should always do this but in IE, text nodes vanishn.className = (n.className == &#34;hidden&#34;) ? &#39;&#39; : &#39;hidden&#39;;link.firstChild.data = n.className == &#34;hidden&#34; ? &#34;show&#34; : &#34;hide&#34;;}/*function grow(link) {var n = link.parentNode.nextSibling;if (n.nodeType == 3 /-* text node *-/) n = n.nextSibling; // we should always do this but in IE, text nodes vanishn.className = (n.className == &#34;large&#34;) ? &#39;&#39; : &#39;large&#39;;link.firstChild.data = n.className == &#34;grow&#34; ? &#34;shrink&